import {useHistory} from "react-router-dom"
const Logout=()=>{
    const history=useHistory()
    const handleLogout=()=>{
        localStorage.setItem("Authorization","")
        history.push("/")
    }
    return(
        <>
            <button onClick={handleLogout}>Log-Out</button>
        </>
    )
}
export default Logout
