import axios from "axios"

export const fetchData=(methodType,endpoint,isAuthToken,data="")=>{
    const defaultUrl="https://ecommercebadar-server.herokuapp.com/"
    const authToken=localStorage.getItem("authorization")
    let headers={}
    if(isAuthToken){
        headers["authorizarion"]=authToken
    }
   return axios({
        url:`${defaultUrl}/${endpoint}`,
        method:methodType,
        headers:headers,
        data:data
    })
}
